 },
 onFailure = { e ->
 _uiState.update {
 it.copy(
 isLoading = false,
 error = "Failed to load stores: ${e.message ?: "Unknown error"}"
 )
 }
 }
 )
 }
 } catch (e: Exception) {
 _uiState.update {
 it.copy(
 isLoading = false,
 error = "Failed to load stores: ${e.message ?: "Unknown error"}"
 )
 }
 }
 }
 }

